mapper.select:
select
stall_id, stall_name, stall_num, can_id, loc_id, ty_id
from stalls


        and stall_id = #{stallId}


        and stall_name like concat('%',#{stallName},'%')


        and stall_num = #{stallNum}


        and can_id = #{canId}


        and loc_id = #{locId}


        and ty_id = #{tyId}

select count(1)
from stalls


        and stall_id = #{stallId}


        and stall_name = #{stallName}


        and stall_num = #{stallNum}


        and can_id = #{canId}


        and loc_id = #{locId}


        and ty_id = #{tyId}
